// Call this function with
//    font: NULL or array length
//    data: NULL or specified size
//    height: STB_FONT_stbfont_BITMAP_HEIGHT or STB_FONT_stbfont_BITMAP_HEIGHT_POW2
//    return value: spacing between lines
static void stb_font_stbfont(stb_fontchar font[STB_FONT_stbfont_NUM_CHARS],
                unsigned char data[STB_FONT_stbfont_BITMAP_HEIGHT][STB_FONT_stbfont_BITMAP_WIDTH],
                int height)
{
    int i,j;
    if (data != 0) {
        unsigned int *bits = stb__stbfont_pixels;
        unsigned int bitpack = *bits++, numbits = 32;
        for (i=0; i < STB_FONT_stbfont_BITMAP_WIDTH*height; ++i)
            data[0][i] = 0;  // zero entire bitmap
        for (j=1; j < STB_FONT_stbfont_BITMAP_HEIGHT-1; ++j) {
            for (i=1; i < STB_FONT_stbfont_BITMAP_WIDTH-1; ++i) {
                unsigned int value;
                if (numbits==0) bitpack = *bits++, numbits=32;
                value = bitpack & 1;
                bitpack >>= 1, --numbits;
                if (value) {
                    if (numbits < 3) bitpack = *bits++, numbits = 32;
                    data[j][i] = (bitpack & 7) * 0x20 + 0x1f;
                    bitpack >>= 3, numbits -= 3;
                } else {
                    data[j][i] = 0;
                }
            }
        }
    }

    // build font description
    if (font != 0) {
        float recip_width = 1.0f / STB_FONT_stbfont_BITMAP_WIDTH;
        float recip_height = 1.0f / height;
        for (i=0; i < STB_FONT_stbfont_NUM_CHARS; ++i) {
            // pad characters so they bilerp from empty space around each character
            font[i].s0 = (stb__stbfont_s[i]) * recip_width;
            font[i].t0 = (stb__stbfont_t[i]) * recip_height;
            font[i].s1 = (stb__stbfont_s[i] + stb__stbfont_w[i]) * recip_width;
            font[i].t1 = (stb__stbfont_t[i] + stb__stbfont_h[i]) * recip_height;
            font[i].x0 = stb__stbfont_x[i];
            font[i].y0 = stb__stbfont_y[i];
            font[i].x1 = stb__stbfont_x[i] + stb__stbfont_w[i];
            font[i].y1 = stb__stbfont_y[i] + stb__stbfont_h[i];
            font[i].advance_int = (stb__stbfont_a[i]+8)>>4;
            font[i].s0f = (stb__stbfont_s[i] - 0.5f) * recip_width;
            font[i].t0f = (stb__stbfont_t[i] - 0.5f) * recip_height;
            font[i].s1f = (stb__stbfont_s[i] + stb__stbfont_w[i] + 0.5f) * recip_width;
            font[i].t1f = (stb__stbfont_t[i] + stb__stbfont_h[i] + 0.5f) * recip_height;
            font[i].x0f = stb__stbfont_x[i] - 0.5f;
            font[i].y0f = stb__stbfont_y[i] - 0.5f;
            font[i].x1f = stb__stbfont_x[i] + stb__stbfont_w[i] + 0.5f;
            font[i].y1f = stb__stbfont_y[i] + stb__stbfont_h[i] + 0.5f;
            font[i].advance = stb__stbfont_a[i]/16.0f;
        }
    }
}

